using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AsyncTester.Services {
    public class NameGenerator : INameGenerator {
        private string[] femaleNames;
        private string[] maleNames;
        private string[] lastNames;
        Random rand = new Random();

        public NameGenerator() {
            femaleNames = MostCommonFemaleNames.Split(',');
            maleNames = MostCommonMaleNames.Split(',');
            lastNames = MostCommonLastNames.Split(',');
        }

        #region Async Methods

        public async Task<string> GenerateNameAsync(Sex sex) {
            string firstName = string.Empty;

            await Task.Factory.StartNew(() => {
                if (sex == Sex.Male) {
                    firstName = maleNames[rand.Next(0, maleNames.Length - 1)];
                }
                else {
                    firstName = femaleNames[rand.Next(0, femaleNames.Length - 1)];
                }
            }, TaskCreationOptions.LongRunning);

            return firstName;
        }

        public async Task<string> GenerateLastNameAsync() {
            string lastName = string.Empty;

            await Task.Factory.StartNew(() => {
                lastName = lastNames[rand.Next(0, lastNames.Length - 1)];
            }, TaskCreationOptions.LongRunning);

            return lastName;
        }

        public async Task<string> GenerateFullNameAsync(Sex sex) {
            string firstName = string.Empty;
            string lastName = string.Empty;

            await Task.Factory.StartNew(() => {
                if (sex == Sex.Male) {
                    firstName = maleNames[rand.Next(0, maleNames.Length - 1)];
                }
                else {
                    firstName = femaleNames[rand.Next(0, femaleNames.Length - 1)];
                }
                lastName = lastNames[rand.Next(0, lastNames.Length - 1)];
            }, TaskCreationOptions.LongRunning);

            return string.Format("{0} {1}", firstName, lastName);
        }

        #endregion

        #region Non Async methods

        public string GenerateName(Sex sex) {
            string firstName = string.Empty;

            if (sex == Sex.Male) {
                firstName = maleNames[rand.Next(0, maleNames.Length - 1)];
            }
            else {
                firstName = femaleNames[rand.Next(0, femaleNames.Length - 1)];
            }

            return firstName;
        }

        public string GenerateLastName() {
            string lastName = string.Empty;
            lastName = lastNames[rand.Next(0, lastNames.Length - 1)];
            return lastName;
        }

        public string GenerateFullName(Sex sex) {
            string firstName = string.Empty;
            string lastName = string.Empty;

            if (sex == Sex.Male) {
                firstName = maleNames[rand.Next(0, maleNames.Length - 1)];
            }
            else {
                firstName = femaleNames[rand.Next(0, femaleNames.Length - 1)];
            }
            lastName = lastNames[rand.Next(0, lastNames.Length - 1)];

            return string.Format("{0} {1}", firstName, lastName);
        }

